mapper.insert:
INSERT INTO joinUser 
	(
	 unum, user_id, user_password, name
	 , jumin, email, addr, phone
	 )
VALUES 
	(
	 User_SEQ.NEXTVAL, #{user_id}, #{user_password}, #{name}
	 , #{jumin}, #{email}, #{addr}, #{phone} 
	)

mapper.update:
UPDATE joinUser SET 
(
 unum = #{unum}, user_id = #{user_id}, user_password = #{user_password}
 , name = #{name}, jumin = #{jumin}, email = #{email}, addr = #{addr}
 , phone = #{phone}
 )
